        <Route path="/tournaments/:id/created" element={<TournamentCreationCompletePage />} />
        <Route path="/tournaments/:id/register" element={<ParticipantRegistrationPage />} />
        <Route path="/tournaments/:id" element={<TournamentPortalPage />} />
      </Routes>
    </BrowserRouter>
  );
};

export default App;

