interface Quiz {
  id: string;
  question: string;
  answer: string;
}

const StyledContainer = styled(Container)(({ theme }) => ({
  marginTop: theme.spacing(4),
  marginBottom: theme.spacing(4),
}));

const StatusPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  marginBottom: theme.spacing(3),
}));

const ParticipantDashboardPage = () => {
  const { tournamentId, participantId } = useParams();
  const [quizzes, setQuizzes] = useState<Quiz[]>([]);
  const [remainingQuestions, setRemainingQuestions] = useState(0);

  useEffect(() => {
    if (!participantId) return;

    const fetchQuizStatus = async () => {
      try {
        const response = await fetch(`/api/participants/${participantId}/quizzes`);
        if (!response.ok) {
          throw new Error('Failed to fetch quiz status');
        }
        const data = await response.json();
        setQuizzes(data.createdQuizzes.map((q: any) => {
          // The question field contains JSON string with question and options
          try {
            const parsedQuestion = JSON.parse(q.question);
            return { ...q, question: parsedQuestion.question };
          } catch (e) {
            // If parsing fails, it might be an old format, just return the raw question
            return q;
          }
        }));
        setRemainingQuestions(data.remainingQuestions);
      } catch (error) {
        console.error(error);
        // Handle error display in UI
      }
    };

    fetchQuizStatus();
  }, [participantId]);

  return (
