import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Button,
  Container,
  Typography,
  Box,
  Paper,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
} from '@mui/material';
import { styled } from '@mui/material/styles';

interface ParticipantStatus {
  id: string;
  name: string;
  created: number;
  required: number;
}

const StyledContainer = styled(Container)(({ theme }) => ({
  marginTop: theme.spacing(4),
  marginBottom: theme.spacing(4),
}));

const HeaderPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  marginBottom: theme.spacing(3),
}));

const OrganizerDashboardPage = () => {
  const { tournamentId } = useParams();
  const portalUrl = `${window.location.origin}/tournaments/${tournamentId}`;

  const [participants, setParticipants] = useState<ParticipantStatus[]>([]);
  const [tournamentName, setTournamentName] = useState('');

  useEffect(() => {
    if (!tournamentId) return;

    const fetchTournamentStatus = async () => {
      try {
        const response = await fetch(`/api/tournaments/${tournamentId}/status`);
        if (!response.ok) {
          throw new Error('Failed to fetch tournament status');
        }
        const data = await response.json();
        setTournamentName(data.tournamentName);
        setParticipants(data.participants);
      } catch (error) {
        console.error(error);
        // TODO: Handle error display in UI
      }
    };

    fetchTournamentStatus();
  }, [tournamentId]);

  const handleStartTournament = () => {
    // TODO: Implement start tournament logic
    alert('大会を開始します！（仮）');
  };

  return (
    <StyledContainer maxWidth="lg">
      <HeaderPaper elevation={3}>
        <Typography variant="h4" component="h1" gutterBottom>
          管理ページ: {tournamentName}
        </Typography>
        <Box>
          <Typography variant="subtitle1">招待URL</Typography>
          <Typography variant="body2" color="text.secondary">
            {portalUrl}
          </Typography>
        </Box>
      </HeaderPaper>

      <Typography variant="h5" component="h2" gutterBottom>
        参加者の問題作成状況
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>参加者名</TableCell>
              <TableCell align="right">作成状況</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {participants.map((p) => (
              <TableRow key={p.id}>
                <TableCell component="th" scope="row">
                  {p.name}
                </TableCell>
                <TableCell align="right">{`${p.created} / ${p.required} 問`}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <Box sx={{ mt: 4, textAlign: 'center' }}>
        <Button variant="contained" color="primary" size="large" onClick={handleStartTournament}>
          この内容で大会を開始する
        </Button>
        <Typography variant="caption" display="block" sx={{ mt: 1 }}>
          一度開始すると、問題の編集や参加者の追加はできなくなります。
        </Typography>
      </Box>
    </StyledContainer>
  );
};

export default OrganizerDashboardPage;

import { describe, it, expect, beforeAll, afterAll } from 'vitest';
import request from 'supertest';
import express from 'express';
import tournamentsRouter from './tournaments'; // Adjust path as needed
import { PrismaClient } from '@prisma/client';

const prisma = new PrismaClient();
const app = express();
app.use(express.json());
app.use('/tournaments', tournamentsRouter);

describe('Tournaments API', () => {
  let tournament;

  beforeAll(async () => {
    // Create a tournament for testing
    tournament = await prisma.tournament.create({
      data: {
        name: 'Test Tournament for API',
        password: 'correct-password',
        questionsPerParticipant: 3,
        points: '10,20,30',
      },
    });
  });

  afterAll(async () => {
    // Clean up the database
    await prisma.tournament.delete({ where: { id: tournament.id } });
    await prisma.$disconnect();
  });

  describe('POST /:id/login', () => {
    it('should return success with correct password', async () => {
      const res = await request(app)
        .post(`/tournaments/${tournament.id}/login`)
        .send({ password: 'correct-password' });
      expect(res.statusCode).toBe(200);
      expect(res.body.success).toBe(true);
    });

    it('should return unauthorized with incorrect password', async () => {
      const res = await request(app)
        .post(`/tournaments/${tournament.id}/login`)
        .send({ password: 'wrong-password' });
      expect(res.statusCode).toBe(401);
      expect(res.body.success).toBe(false);
    });
  });

  describe('GET /:id/status', () => {
    it('should return tournament status with participant progress', async () => {
      // Create a participant and a quiz for the test
      const participant = await prisma.participant.create({
        data: {
          name: 'Test Participant',
          password: 'pw',
          tournamentId: tournament.id,
        },
      });
      await prisma.quiz.create({
        data: {
          question: 'Test Question',
          answer: 'Test Answer',
          point: 10,
          tournamentId: tournament.id,
          participantId: participant.id,
        },
      });

      const res = await request(app).get(`/tournaments/${tournament.id}/status`);

      expect(res.statusCode).toBe(200);
      expect(res.body.tournamentName).toBe('Test Tournament for API');
      expect(res.body.participants).toHaveLength(1);
      expect(res.body.participants[0].name).toBe('Test Participant');
      expect(res.body.participants[0].created).toBe(1);
      expect(res.body.participants[0].required).toBe(3);

      // Cleanup
      await prisma.quiz.deleteMany({ where: { participantId: participant.id } });
      await prisma.participant.delete({ where: { id: participant.id } });
    });
  });
});

