import { render, screen, waitFor } from '@testing-library/react';
import { describe, it, expect, vi } from 'vitest';
import { MemoryRouter, Route, Routes } from 'react-router-dom';
import OrganizerDashboardPage from './OrganizerDashboardPage';

describe('OrganizerDashboardPage', () => {
  const mockData = {
    tournamentName: 'My Test Tournament',
    participants: [
      { id: '1', name: 'Alice', created: 2, required: 3 },
      { id: '2', name: 'Bob', created: 3, required: 3 },
    ],
  };

  it('should fetch and display tournament status correctly', async () => {
    // Mock the global fetch function
    global.fetch = vi.fn().mockResolvedValue({
      ok: true,
      json: () => Promise.resolve(mockData),
    });

    render(
      <MemoryRouter initialEntries={['/tournaments/test-id/admin']}>
        <Routes>
          <Route path="/tournaments/:tournamentId/admin" element={<OrganizerDashboardPage />} />
        </Routes>
      </MemoryRouter>
    );

    // Wait for the API call and rendering to complete
    await waitFor(() => {
      expect(screen.getByText(`管理ページ: ${mockData.tournamentName}`)).toBeInTheDocument();
    });

    // Check if participant data is rendered correctly
    expect(screen.getByText('Alice')).toBeInTheDocument();
    expect(screen.getByText('2 / 3 問')).toBeInTheDocument();
    expect(screen.getByText('Bob')).toBeInTheDocument();
    expect(screen.getByText('3 / 3 問')).toBeInTheDocument();

    // Check if the API was called with the correct URL
    expect(global.fetch).toHaveBeenCalledWith('/api/tournaments/test-id/status');
  });
});

import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import { describe, it, expect, vi } from 'vitest';
import { MemoryRouter, Route, Routes } from 'react-router-dom';
import TournamentPortalPage from './TournamentPortalPage';

describe('TournamentPortalPage', () => {
  it('should open login modal, attempt login, and navigate on success', async () => {
    // Mock the global fetch function
    global.fetch = vi.fn().mockResolvedValue({
      ok: true,
      json: () => Promise.resolve({ success: true }),
    });

    render(
      <MemoryRouter initialEntries={['/tournaments/test-id']}>
        <Routes>
          <Route path="/tournaments/:id" element={<TournamentPortalPage />} />
        </Routes>
      </MemoryRouter>
    );

    // 1. Click the organizer login button
    fireEvent.click(screen.getByRole('button', { name: '主催者としてログイン' }));

    // 2. Assert the modal is open
    expect(screen.getByRole('dialog')).toBeInTheDocument();
    expect(screen.getByLabelText('管理用パスワード')).toBeInTheDocument();

    // 3. Fill in the password and click login
    fireEvent.change(screen.getByLabelText('管理用パスワード'), {
      target: { value: 'password123' },
    });
    fireEvent.click(screen.getByRole('button', { name: 'ログイン' }));

    // 4. Assert the API was called correctly
    await waitFor(() => {
      expect(global.fetch).toHaveBeenCalledWith('/api/tournaments/test-id/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ password: 'password123' }),
      });
    });
  });
});

import { test, expect, APIRequestContext } from '@playwright/test';

let tournamentId: string;

// Helper function to create a tournament and participants via API
const setupTestTournament = async (request: APIRequestContext) => {
  const tournamentRes = await request.post('/api/tournaments', {
    data: {
      name: `Organizer Flow Test Tournament ${Date.now()}` ,
      password: 'organizer-password',
      questionsPerParticipant: 2,
      points: '10,20',
      regulation: 'E2E Test Regulation',
    },
  });
  const tournament = await tournamentRes.json();
  tournamentId = tournament.id;

  // Create participants
  await request.post(`/api/tournaments/${tournamentId}/participants`, { data: { name: 'Participant A' } });
  const participantB_Res = await request.post(`/api/tournaments/${tournamentId}/participants`, { data: { name: 'Participant B' } });
  const participantB = await participantB_Res.json();

  // Create a quiz for one of the participants
  await request.post('/api/quizzes', {
    data: {
      question: 'Quiz by B',
      options: JSON.stringify(['A', 'B']),
      answer: 'A',
      point: 10,
      tournamentId,
      participantId: participantB.id,
    }
  });
};

test.describe('Organizer Login and Dashboard Flow', () => {
  test.beforeAll(async ({ request }) => {
    await setupTestTournament(request);
  });

  test('should allow an organizer to log in and see the correct dashboard status', async ({ page }) => {
    // 1. Navigate to the tournament portal page
    await page.goto(`/tournaments/${tournamentId}`);

    // 2. Open the organizer login modal
    await page.getByRole('button', { name: '主催者としてログイン' }).click();

    // 3. Enter the correct password and log in
    await page.getByLabel('管理用パスワード').fill('organizer-password');
    await page.getByRole('button', { name: 'ログイン' }).click();

    // 4. Assert navigation to the admin dashboard
    await page.waitForURL(`/tournaments/${tournamentId}/admin`);
    await expect(page.getByRole('heading', { name: /管理ページ:/ })).toBeVisible();

    // 5. Verify the displayed data
    await expect(page.getByText('Participant A')).toBeVisible();
    await expect(page.getByText('0 / 2 問')).toBeVisible();
    await expect(page.getByText('Participant B')).toBeVisible();
    await expect(page.getByText('1 / 2 問')).toBeVisible();
  });
});

import { useState } from 'react';
import { Button, Container, Typography, Box, Dialog, DialogActions, DialogContent, DialogContentText, TextField } from '@mui/material';
import { styled } from '@mui/material/styles';
import { Link, useParams, useNavigate } from 'react-router-dom';

const StyledContainer = styled(Container)(({ theme }) => ({
  textAlign: 'center',
  marginTop: theme.spacing(8),
}));

const TournamentPortalPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const [password, setPassword] = useState('');

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
    setPassword('');
  };

  const handleLogin = async () => {
    try {
      const response = await fetch(`/api/tournaments/${id}/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ password }),
      });

      if (response.ok) {
        alert('主催者としてログインしました！');
        if (navigate) {
          navigate(`/tournaments/${id}/admin`);
        }
        handleClose();
      } else {
        alert('パスワードが違います。');
        setPassword('');
      }
    } catch (error) {
      console.error(error);
      alert('ログイン中にエラーが発生しました。');
    }
  };

  return (
    <StyledContainer maxWidth="md">
      <Typography variant="h4" component="h1" gutterBottom>
        大会ポータル
      </Typography>
      <Typography variant="h6" color="textSecondary" paragraph>
        参加方法を選択してください
      </Typography>
      <Box sx={{ mt: 4, display: 'flex', justifyContent: 'center', gap: 2 }}>
        <Button variant="outlined" color="primary" size="large" onClick={handleClickOpen}>
          主催者としてログイン
        </Button>
        <Button component={Link} to={`/tournaments/${id}/register`} variant="contained" color="primary" size="large">
          参加者として新規登録
        </Button>
      </Box>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>主催者ログイン</DialogTitle>
        <DialogContent>
          <DialogContentText>
            大会作成時に設定した管理用パスワードを入力してください。
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="password"
            label="管理用パスワード"
            type="password"
            fullWidth
            variant="standard"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleLogin()}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>キャンセル</Button>
          <Button onClick={handleLogin}>ログイン</Button>
        </DialogActions>
      </Dialog>
    </StyledContainer>
  );
};

export default TournamentPortalPage;

