import { test, expect, APIRequestContext } from '@playwright/test';

let tournamentId: string;

// Helper function to create a tournament and participants via API
const setupTestTournament = async (request: APIRequestContext) => {
  const tournamentRes = await request.post('/api/tournaments', {
    data: {
      name: `Organizer Flow Test Tournament ${Date.now()}` ,
      password: 'organizer-password',
      questionsPerParticipant: 2,
      points: '10,20',
      regulation: 'E2E Test Regulation',
    },
  });
  const tournament = await tournamentRes.json();
  tournamentId = tournament.id;

  // Create participants
  await request.post(`/api/tournaments/${tournamentId}/participants`, { data: { name: 'Participant A' } });
  const participantB_Res = await request.post(`/api/tournaments/${tournamentId}/participants`, { data: { name: 'Participant B' } });
  const participantB = await participantB_Res.json();

  // Create a quiz for one of the participants
  await request.post('/api/quizzes', {
    data: {
      question: 'Quiz by B',
      options: JSON.stringify(['A', 'B']),
      answer: 'A',
      point: 10,
      tournamentId,
      participantId: participantB.id,
    }
  });
};

test.describe('Organizer Login and Dashboard Flow', () => {
  test.beforeAll(async ({ request }) => {
    await setupTestTournament(request);
  });

  test('should allow an organizer to log in and see the correct dashboard status', async ({ page }) => {
    // 1. Navigate to the tournament portal page
    await page.goto(`/tournaments/${tournamentId}`);

    // 2. Open the organizer login modal
    await page.getByRole('button', { name: '主催者としてログイン' }).click();

    // 3. Enter the correct password and log in
    await page.getByLabel('管理用パスワード').fill('organizer-password');

    // Listen for the response and log the status
    page.on('response', response => {
      if (response.url().includes('/login')) {
        console.log(`Login API Response Status: ${response.status()}`);
      }
    });

    await page.getByRole('button', { name: 'ログイン' }).click();

    // 4. Assert navigation to the admin dashboard
    await page.waitForURL(`/tournaments/${tournamentId}/admin`);
    await expect(page.getByRole('heading', { name: /管理ページ:/ })).toBeVisible();

    // 5. Verify the displayed data
    await expect(page.getByText('Participant A')).toBeVisible();
    await expect(page.getByText('0 / 2 問')).toBeVisible();
    await expect(page.getByText('Participant B')).toBeVisible();
    await expect(page.getByText('1 / 2 問')).toBeVisible();
  });
});

