import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Container, Typography, Box, Paper, Grid, ButtonBase } from '@mui/material';
import { styled } from '@mui/material/styles';

const StyledContainer = styled(Container)(({ theme }) => ({
  marginTop: theme.spacing(4),
  marginBottom: theme.spacing(4),
}));

const QuizCard = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(2),
  textAlign: 'center',
  width: '100%',
  height: '100px',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  cursor: 'pointer',
}));

const QuizBoardPage = () => {
  const { tournamentId } = useParams();
  const navigate = useNavigate();
  const [tournament, setTournament] = useState<any>(null);

  useEffect(() => {
    if (!tournamentId) return;
    const fetchBoardData = async () => {
      try {
        const response = await fetch(`/api/tournaments/${tournamentId}/board`);
        if (!response.ok) {
          throw new Error('Failed to fetch board data');
        }
        const data = await response.json();
        setTournament(data);
      } catch (error) {
        console.error(error);
      }
    };
    fetchBoardData();
  }, [tournamentId]);

  const handleQuizSelect = (quizId: string) => {
    // TODO: Navigate to the quiz display page
    alert(`Quiz ${quizId} selected`);
  };

  if (!tournament) {
    return <Typography>Loading...</Typography>;
  }

  const points = tournament.points.split(',').map(Number);

  return (
    <StyledContainer maxWidth="xl">
      <Typography variant="h3" component="h1" align="center" gutterBottom>
        {tournament.name}
      </Typography>
      <Grid container spacing={2} sx={{ mt: 4 }}>
        {/* Header Row */}
        <Grid item xs={2} />
        {tournament.participants.map((p: any) => (
          <Grid item xs key={p.id} textAlign="center">
            <Typography variant="h6">{p.name}</Typography>
          </Grid>
        ))}

        {/* Quiz Rows */}
        {points.map((point: number, rowIndex: number) => (
          <Grid container item spacing={2} key={rowIndex} alignItems="center">
            <Grid item xs={2} textAlign="right">
              <Typography variant="h5">{point}点</Typography>
            </Grid>
            {tournament.participants.map((p: any) => {
              const quiz = p.quizzes.find((q: any) => q.point === point);
              return (
                <Grid item xs key={`${p.id}-${point}`}>
                  {quiz ? (
                    <ButtonBase sx={{ width: '100%' }} onClick={() => handleQuizSelect(quiz.id)}>
                      <QuizCard elevation={3}>
                        <Typography variant="h6">{point}点</Typography>
                      </QuizCard>
                    </ButtonBase>
                  ) : (
                    <QuizCard elevation={1} sx={{ backgroundColor: 'grey.200' }} />
                  )}
                </Grid>
              );
            })}
          </Grid>
        ))}
      </Grid>
    </StyledContainer>
  );
};

export default QuizBoardPage;

import { BrowserRouter, Routes, Route } from 'react-router-dom';
import ServiceTopPage from './pages/ServiceTopPage';
import TournamentCreationPage from './pages/TournamentCreationPage';
import TournamentCreationCompletePage from './pages/TournamentCreationCompletePage';
import TournamentPortalPage from './pages/TournamentPortalPage';
