import { test, expect, APIRequestContext } from '@playwright/test';

// Helper function to create a tournament via API
const createTournament = async (request: APIRequestContext) => {
  const response = await request.post('/api/tournaments', {
    data: {
      name: `Dashboard Test Tournament ${Date.now()}` ,
      password: 'test-password',
      questionsPerParticipant: 3,
      points: '10,20,30',
      regulation: 'E2E Test Regulation',
    },
  });
  return await response.json();
};

// Helper function to create a participant via API
const createParticipant = async (request: APIRequestContext, tournamentId: string) => {
  const response = await request.post(`/api/tournaments/${tournamentId}/participants`, {
    data: {
      name: `Test Participant ${Date.now()}`
    }
  });
  return await response.json();
};

// Helper function to create a quiz via API
const createQuiz = async (request: APIRequestContext, tournamentId: string, participantId: string, questionNumber: number) => {
  const response = await request.post('/api/quizzes', {
    data: {
      questionText: `Question ${questionNumber}` ,
      answerText: 'A',
      point: 10,
      tournamentId,
      participantId,
    }
  });
  return await response.json();
};

test.describe('参加者ダッシュボード', () => {

  test('シナリオ1: クイズが一件も作成されていない場合に正しいステータスが表示されること', async ({ page, request }) => {
    const tournament = await createTournament(request);
    const participant = await createParticipant(request, tournament.id);

    await page.goto(`/tournaments/${tournament.id}/participants/${participant.id}`);

    await expect(page.getByText('あと 3 問、作成してください。')).toBeVisible();
    await expect(page.getByText('作成済みの問題')).toBeVisible();
    // Expect the list of created quizzes to be empty
    const listItems = await page.locator('ul > div').count();
    expect(listItems).toBe(0);
  });

  test('シナリオ2: クイズがいくつか作成済みの場合に正しいステータスが表示されること', async ({ page, request }) => {
    const tournament = await createTournament(request);
    const participant = await createParticipant(request, tournament.id);
    const quiz = await createQuiz(request, tournament.id, participant.id, 1);

    await page.goto(`/tournaments/${tournament.id}/participants/${participant.id}`);

    await expect(page.getByText('あと 2 問、作成してください。')).toBeVisible();
    await expect(page.getByText('作成済みの問題')).toBeVisible();
    await expect(page.getByText(quiz.questionText)).toBeVisible();
  });

  test('シナリオ3: すべてのクイズが作成済みの場合に正しいステータスが表示されること', async ({ page, request }) => {
    const tournament = await createTournament(request);
    const participant = await createParticipant(request, tournament.id);
    await createQuiz(request, tournament.id, participant.id, 1);
    await createQuiz(request, tournament.id, participant.id, 2);
    await createQuiz(request, tournament.id, participant.id, 3);

    await page.goto(`/tournaments/${tournament.id}/participants/${participant.id}`);

    await expect(page.getByText('あと 0 問、作成してください。')).toBeVisible();
    await expect(page.getByText('作成済みの問題')).toBeVisible();
    const listItems = await page.locator('ul > div').count();
    expect(listItems).toBe(3);
  });
});

import { test, expect, Page } from '@playwright/test';

const createTournamentFromUI = async (page: Page, tournamentName: string) => {
  await page.goto('/');
  await page.getByRole('link', { name: 'クイズ大会を新しく作成する' }).click();
  await page.waitForURL('/tournaments/new');

  await page.getByRole('textbox', { name: '大会名' }).fill(tournamentName);
  await page.getByRole('textbox', { name: '管理用パスワード' }).fill('organizer-password');
  await page.getByRole('textbox', { name: '参加者1人あたりの問題作成数' }).fill('2');
  await page.getByRole('textbox', { name: '各問題の配点 (カンマ区切り)' }).fill('10,20');
  await page.getByRole('textbox', { name: 'レギュレーション' }).fill('E2E Test Regulation');

  await page.getByRole('button', { name: 'この内容で大会を作成する' }).click();

  await page.waitForURL(new RegExp('/tournaments/.*/created'));
  const url = page.url();
  return url.split('/')[4]; // Extract tournament ID from URL
};

const registerParticipant = async (page: Page, tournamentId: string, participantName: string) => {
  await page.goto(`/tournaments/${tournamentId}`);
  await page.getByRole('link', { name: '参加者として新規登録' }).click();
  await page.waitForURL(`/tournaments/${tournamentId}/register`);
  await page.getByLabel('あなたの名前').fill(participantName);
  await page.getByRole('button', { name: 'この名前で参加する' }).click();
  await page.waitForURL(`/tournaments/${tournamentId}/quizzes/new`);
};

test.describe('主催者ログインとダッシュボードフロー', () => {
  test('主催者がログインして正しいダッシュボードのステータスを確認できること', async ({ page }) => {
    const tournamentName = `Organizer UI Flow Test ${Date.now()}`;

    // 1. Create a tournament via UI and get its ID
    const tournamentId = await createTournamentFromUI(page, tournamentName);

    // 2. Register participants
    await registerParticipant(page, tournamentId, 'Participant A');
    await registerParticipant(page, tournamentId, 'Participant B');

    // 3. Navigate to the portal and log in as organizer
    await page.goto(`/tournaments/${tournamentId}`);
    await page.getByRole('button', { name: '主催者としてログイン' }).click();
    await page.getByLabel('管理用パスワード').fill('organizer-password');
    await page.getByRole('button', { name: 'ログイン' }).click();

    // 4. Assert navigation to the admin dashboard and verify content
    await expect(page.getByRole('heading', { name: `管理ページ: ${tournamentName}` })).toBeVisible();

    // 5. Verify the displayed data
    await expect(page.getByText('Participant A')).toBeVisible();
    await expect(page.getByText('0 / 2 問')).toBeVisible();
    await expect(page.getByText('Participant B')).toBeVisible();
    // Note: We can't easily test the submission count without creating quizzes via UI,
    // so we just check for presence.
  });
});

import { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import {
  Button,
  Container,
  Typography,
  Box,
  Paper,
  List,
  ListItem,
  ListItemText,
  Divider,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import { Quiz } from '../models/Quiz';

const StyledContainer = styled(Container)(({ theme }) => ({
  marginTop: theme.spacing(4),
  marginBottom: theme.spacing(4),
}));

const StatusPaper = styled(Paper)(({ theme }) => ({
  padding: theme.spacing(3),
  marginBottom: theme.spacing(3),
}));

const ParticipantDashboardPage = () => {
  const { tournamentId, participantId } = useParams();
  const [quizzes, setQuizzes] = useState<Quiz[]>([]);
  const [remainingQuestions, setRemainingQuestions] = useState(0);

  useEffect(() => {
    if (!participantId) return;

    const fetchQuizStatus = async () => {
      try {
        const response = await fetch(`/api/participants/${participantId}/quizzes`);
        if (!response.ok) {
          throw new Error('Failed to fetch quiz status');
        }
        const data = await response.json();
        setQuizzes(data.createdQuizzes.map((q: any) => { // eslint-disable-line @typescript-eslint/no-explicit-any
          // Now questionText is directly available, no need to parse JSON
          return { ...q, question: q.questionText };
        }));
        setRemainingQuestions(data.remainingQuestions);
      } catch (error) {
        console.error(error);
        // TODO: Handle error display in UI
      }
    };

    fetchQuizStatus();
  }, [participantId]);

  return (
    <StyledContainer maxWidth="md">
      <Typography variant="h4" component="h1" gutterBottom>
        参加者ダッシュボード
      </Typography>

      <StatusPaper elevation={3}>
        <Typography variant="h6" gutterBottom>
          問題作成ステータス
        </Typography>
        <Typography variant="body1">
          あと <Typography component="span" variant="h5" color="secondary">{remainingQuestions}</Typography> 問、作成してください。
        </Typography>
        <Box sx={{ mt: 2 }}>
          <Button 
            component={Link} 
            to={`/tournaments/${tournamentId}/quizzes/new`}
            variant="contained" 
            color="primary">
            新しい問題を作成する
          </Button>
        </Box>
      </StatusPaper>

      <Typography variant="h5" component="h2" gutterBottom>
        作成済みの問題
      </Typography>
      <Paper>
        <List>
          {quizzes.map((quiz, index) => (
            <div key={quiz.id}>
              <ListItem>
                <ListItemText 
                  primary={quiz.question} 
                  secondary={`正解: ${quiz.answer}`}
                />
                {/* TODO: Add Edit/Delete buttons */}
              </ListItem>
              {index < quizzes.length - 1 && <Divider />}
            </div>
          ))}
        </List>
      </Paper>
    </StyledContainer>
  );
};

