          <Button 
            component={Link} 
            to={`/tournaments/${tournamentId}/quizzes/new`}
            variant="contained" 
            color="primary">
            新しい問題を作成する
          </Button>
        </Box>
      </StatusPaper>

      <Typography variant="h5" component="h2" gutterBottom>
        作成済みの問題
      </Typography>
      <Paper>
        <List>
          {quizzes.map((quiz, index) => (
            <div key={quiz.id}>
              <ListItem>
                <ListItemText 
                  primary={quiz.questionText} 
                  secondary={`正解: ${quiz.answerText}`}
                />
                {/* TODO: Add Edit/Delete buttons */}
              </ListItem>
              {index < quizzes.length - 1 && <Divider />}
            </div>
          ))}
        </List>
      </Paper>
    </StyledContainer>
  );
};

export default ParticipantDashboardPage;
